content.CONV_K_MISSION_1C:
[
   {
     "idNpc": 0,
     "text": "Я ждал слишком долго."
   },
   {
     "idNpc": 0,
     "text": "Какие-то проблемы?"
   },
   {
     "idNpc": 1,
     "text": "Я иду."
   },
   {
     "idNpc": 0,
     "text": "Отлично"
   },
   {
     "idNpc": 0,
     "text": "Цель прибудет примерно через полчаса."
   },
{
     "idNpc": 0,
     "text": "К тому времени мы должны быть готовы, одеты, в балаклаве и прочем."
   },
   {
     "idNpc": 0,
     "text": "Паркуем машину рядом и когда он выходит из машины подходим сзади."
   },
   {
     "idNpc": 1,
     "text": "Хорошо."
   },
   {
     "idNpc": 0,
     "text": "Ты затыкаешь ему рот, и мы сажаем его на заднее сиденье, когда я завожу машину, ты надеваешь на него кляп и связываешь."
   },
{
     "idNpc": 0,
     "text": "Не опаздывай."
   },
   {
     "idNpc": 1,
     "text": "Я уже здесь."
   }
]

content.CONV_BK_MISSION_1:
[
  {
    "idNpc": 0,
    "text": "Даааааа!!!"
  },
  {
    "idNpc": 1,
    "text": "???"
  },
  {
    "idNpc": 1,
    "text": "Ты в порядке? xD"
  },
  {
    "idNpc": 0,
    "text": "Я это сделал!!!"
  },
  {
    "idNpc": 1,
    "text": "Ну давай уже..."
  },
{
    "idNpc": 0,
    "text": "Да, я только что закончил загрузку"
  },
  {
    "idNpc": 0,
    "text": "По счастливой случайности в конце концов мне удалось проникнуть в систему."
  },
  {
    "idNpc": 0,
    "text": "Наконец-то xDD"
  },
  {
    "idNpc": 1,
    "text": "Ты шутишь?"
  },
{
    "idNpc": 0,
    "text": "НЕТ"
  },
  {
    "idNpc": 1,
    "text": "Ты открывал? Отправь сейчас же!"
  },
  {
    "idNpc": 0,
    "text": "Теперь я собираюсь запустить его. Я должен был это отпраздновать."
  },
  {
    "idNpc": 0,
    "text": "Четыре дня я таращился в экран, я уже почти сдался хах"
  },
{
    "idNpc": 0,
    "text": "Ой, кто-то стучит в дверь, я скоро вернусь."
  },
  {
    "idNpc": 1,
    "text": "Как вовремя, отправь мне сначала файл"
  },
  {
    "idNpc": 1,
    "text": "Эй, не оставляй меня с интригой!"
  },
  {
    "idNpc": 1,
    "text": "Это не смешно, прошло уже больше часа."
  },
  {
    "idNpc": 1,
    "text": "Ты здесь??"
  }
]

